class DropdownToggle{constructor(e){this.links=document.querySelectorAll(e.dropdownLinks),this.dropdowns=document.querySelectorAll(e.dropdownAreas),this.currentOpened=void 0,this.init()}init(){this.links.forEach(e=>e.addEventListener("click",e=>{this.activate(e)}))}activate(e){let t=e.currentTarget;this.currentOpened===t?this.closeCurrent():(this.closeAll(),this.openSelected(t))}closeAll(){this.links.forEach(e=>{e.classList.contains("active")&&(e.classList.remove("active"),e.nextElementSibling.classList.remove("active"),e.nextElementSibling.style.removeProperty("max-height"))})}closeCurrent(){this.currentOpened.classList.remove("active"),this.currentOpened.nextElementSibling.classList.remove("active"),this.currentOpened.nextElementSibling.style.removeProperty("max-height"),this.currentOpened=void 0}openSelected(e){e.classList.add("active"),e.nextElementSibling.classList.add("active"),e.nextElementSibling.style.maxHeight=`${e.nextElementSibling.scrollHeight}px`,this.currentOpened=e}}