class ScrollSpy{constructor(e){this.spySections=document.querySelectorAll(e.spySections),this.spyOptions=e.spyOptions,this.stopWorkingBiggerThan=e.stopWorkingBiggerThan,this.observeUI(this)}observeUI(e){const s=new IntersectionObserver(function(s,t){if(!(window.innerWidth<=e.stopWorkingBiggerThan)&&s[0].isIntersecting&&window.innerWidth>=960){const t=s[0].target.id;e.handleUI(t)}},e.spyOptions);this.spySections.forEach(e=>{s.observe(e)})}handleUI(e){document.querySelector(".active").classList.remove("active"),document.querySelector("a[href*="+e+"]").classList.add("active")}}